#import <React/RCTBridgeModule.h>
#import <React/RCTEventEmitter.h>
#import <Foundation/Foundation.h>
#import <React/RCTViewManager.h>
#import <React/RCTLinkingManager.h>

@interface RCT_EXTERN_MODULE(PhonePePaymentSDK, RCTLinkingManager);

/*
 This is the macro method or bridge exposed to interact with init method at the React side.
*/
RCT_EXTERN_METHOD(init:(NSString *)environment
                  merchantId:(NSString *)merchantId
                  appId:(NSString *)appId
                  enableLogging:(BOOL *)enableLogging
                  withResolver:(RCTPromiseResolveBlock)resolve
                  withRejecter:(RCTPromiseRejectBlock)reject)

/*
 This is the macro method or bridge exposed to interact with startPG method at the React side.
 */
RCT_EXTERN_METHOD(startTransaction:(NSString *)body
                  checksum:(NSString *)checksum
                  packageName:(NSString *)packageName
                  appSchema:(NSString *)appSchema
                  withResolver:(RCTPromiseResolveBlock)resolve
                  withRejecter:(RCTPromiseRejectBlock)reject)

RCT_EXTERN_METHOD(isPhonePeInstalled:(RCTPromiseResolveBlock)resolve
                  withRejecter:(RCTPromiseRejectBlock)reject)

RCT_EXTERN_METHOD(isPaytmAppInstalled:(RCTPromiseResolveBlock)resolve
                  withRejecter:(RCTPromiseRejectBlock)reject)

RCT_EXTERN_METHOD(isGPayAppInstalled:(RCTPromiseResolveBlock)resolve
                  withRejecter:(RCTPromiseRejectBlock)reject)

+ (BOOL)requiresMainQueueSetup
{
  return NO;
}

@end

